function ConstOperation(){this.make=function(a){return["const",a]},this.execute=function(a){return a[1]}}function EqualOperation(){this.make=function(a,b){return["eq",a,b]},this.execute=function(a,b){return b.operations[a[1][0]].execute(a[1],b)==b.operations[a[2][0]].execute(a[2],b)}}function GetOperation(){this.make=function(a){return["get",a]},this.execute=function(a,b){return b.record[a[1]]}}function OrOperation(){this.make=function(a){return["or",a]},this.execute=function(a,b){for(var c=0;c<a[1].length;c++)if(b.operations[a[1][c][0]].execute(a[1][c],b))return!0;return!1}}function ParameterOperation(){this.make=function(a){return["param",a]},this.execute=function(a,b){return b.parameters[a[1]]}}function SelectOperation(){this.make=function(a,b){return["select",a,b]},this.execute=function(a,b){var c=b.operations[a[1][0]].execute(a[1],b).filter(function(c){return b.record=c,b.operations[a[2][0]].execute(a[2],b)});return b.record=null,c}}function TableOperation(){this.make=function(a){return["table",a]},this.execute=function(a,b){return b.file.tables[a[1]]}}function TrueOperation(){this.make=function(){return["true"]},this.execute=function(){return!0}}